	.container{
		display: grid;
		grid-template-columns: 200px 1fr;
	}
	.user-image{
		grid-column-end: span 1;
	}
	.user-info{
		grid-column-end: span 1;
	}
	@media screen and (max-width: 768px) {
		.container{
			display: grid;
			grid-template-columns: 1fr;
			grid-template-rows: 1fr;
			grid-gap: 15px;
		}
		.user-info{
			grid-column-end: span 1;
			grid-row-end: span 1;
		}
	}
</style>